static BOOL update_string(char** current, const char* next, size_t next_len, BOOL copy,
                          BOOL cleanup)
{
	if (cleanup)
	{
		if (*current)
			memset(*current, 0, strlen(*current));
		free(*current);
	}

	if (!next && (next_len > 0))
	{
		*current = calloc(next_len, 1);
		return (*current != NULL);
	}

	if (copy)
		*current = (next ? strndup(next, next_len) : NULL);
	else
		*current = next;
	return !next || (*current != NULL);
}

